import React from "react";
import { useRef} from "react";


function Header(){
    const txtTitle = useRef();
    const hexColor = useRef();
    console.log(txtTitle);
    const submit = (e) => {
        e.preventDefault();

    }
    return(
       <form onSubmit={submit}>
           <input type="text" placeholder="color title..."/>
           <input type="color"/>
           <button>add</button>
       </form>
    );
}
export default Header;
